sections.css:
.logo
{
    color: #C9C9C9;
    margin: 0;
    margin-right: -10px;
    font: 32px/1 Monaco;
    text-align: center;
    float: right;
}

.logo .var
{
    color: #66C9EE;
}

.logo .ya
{
    color: #82B964;
}

@-webkit-keyframes twinkly {
    0%   { background: #D24D33; }
    100% { background: lightgrey; }
}
@-moz-keyframes twinkly {
    0%   { background: #D24D33; }
    100% { background: lightgrey; }
}

.logo .cursor
{
    width: 3px;
    height: 45px;
    margin-bottom: -10px;
    display: inline-block;
    -webkit-animation: twinkly .5s alternate infinite;
    -moz-animation: twinkly .5s alternate infinite;
}

.highlight pre,
code
{
    font-family: Monaco;
    padding: .1em .25em;
}

.link
{
    color: #3B8DBD;
}

.languages
{
    display: block;
    padding: 0;
    margin: .5em 0 0;
    list-style: none;
    float: right;
}
.languages__lang
{
    margin: 0;
    padding: 0;
    list-style: none;
    display: inline-block;
    width: 24px;
    height: 24px;
}
.languages__kludge
{
    color: #666;
}

.social-ico
{
    padding-top: 2em;
}

@media screen and (max-width: 768px) {
    .social-ico
    {
        padding-top: 1em;
    }

    .social-ico .menu-vert__item
    {
        display:inline-block;
    }
}

.social-ico__ico
{
    width: 44px;
    height: 44px;
    display: block;
    margin: .5em 0 0 auto;
    border-radius: 50%;
    background-position: 50% 50%;
}

.social-ico__ico_in-text
{
    width: 22px;
    height: 22px;
    background-size: 40px 40px;
    background-repeat: no-repeat;
    display: inline-block;
    margin: 0 0 0 0.5em;
    vertical-align: middle;
}

@media screen and (max-width: 768px) {
    .social-ico__ico
    {
        margin: 0;
        margin-right: 0.5em;
    }
}

.social-ico__ico_type_rss
{
    background-image: url('social-ico__rss.png');
}

@media screen and (max-width: 768px) {
    .social-ico__ico_type_rss
    {
        margin-right: 2em;
    }
}

.social-ico__ico_type_github
{
    background-image: url('social-ico__github.png');
}

.social-ico__ico_type_twitter
{
    background-image: url('social-ico__twitter.png');
}

.social-ico__ico_type_facebook
{
    background-image: url('social-ico__facebook.png');
}

.social-ico__ico_type_linkedin
{
    background-image: url('social-ico__linkedin.png');
}

.social-ico__text
{
    float: left;
    line-height: 1.25em;
    font-size: .8em;
    padding-top: .3em;
}

@media screen and (max-width: 768px) {
    .social-ico__text
    {
        float: right;
        margin-right: 1em;
    }
    .social-ico__text .menu-vert__item
    {
        display: list-item;
    }
}

.social-ico__rss-text
{
    color: #DF620E;
}

.share
{
    height: 26px;
    float: right;
}

.b-share-btn__wrap
{
    display: block;
}

.header
{
    -webkit-box-ordinal-group: 1;
    -moz-box-ordinal-group: 1;
    -ms-flex-order: 1;
    -webkit-order: 1;
    order: 1;
    width: 1200px;
    margin: 2em auto 3em;
    text-align: left;
    padding: 0;
    display: table;
}

@media screen and (max-width: 768px) {
    .header {
        width: 720px;
    }
}

.header__left
{
    padding: 0;
    margin: 0;
    width: auto;
    display: table-cell;
    text-align: right;
    width: 778px;
    line-height: 4em;
}

@media screen and (max-width: 768px) {
    .header__left {
        width: 100%;
    }
}

.header__center
{
    padding: 0;
    margin: 0;
    display: table-cell;
    width: 201px;
}

.header__right
{
    width: 325px;
    padding-left: 20px;
    display: table-cell;
    line-height: 4em;
}

@media screen and (max-width: 768px) {
    .header__right {
        display: none;
    }
}

.header__item
{
    border-radius: 50%;
    margin-bottom: 30px;
    margin-right: 3px;
    display: inline-block;
}

.footer__outer
{
    background: #999;
}

.footer
{
    width: 1200px;
    margin: 2em auto 0;
    padding: 1.5em 0;
    text-align: left;
    font-size: .8em;
    color: white;
    font-family: Monaco;
    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-flow: row wrap;
    -moz-flex-flow: row wrap;
    -ms-flex-flow: row wrap;
    flex-flow: row wrap;
}

@media screen and (max-width: 768px) {
    .footer {
        width: 710px;
        text-align: center;
    }
}

.footer__left
{
    -webkit-box-ordinal-group: 1;
    -moz-box-ordinal-group: 1;
    -ms-flex-order: 1;
    -webkit-order: 1;
    order: 1;
    width: 13%;
    padding-left: 5em;
    padding-left: 10%;
}

@media screen and (max-width: 768px) {
    .footer__left
    {
        padding-left: 0;
        width: 100%;
        margin-bottom: 0.5em;
    }

    .footer__left .menu-vert__item
    {
        display: inline-block;
    }

    .footer__left .menu-vert__item:after
    {
        content: '·';
        margin: 0 0.5em;
    }

    .footer__left .menu-vert__item:last-child::after
    {
        display: none;
    }
}

.footer__center
{
    -webkit-box-ordinal-group: 2;
    -moz-box-ordinal-group: 2;
    -ms-flex-order: 2;
    -webkit-order: 2;
    order: 2;
    width: 48.5%;
    padding-left: 2em;
    padding-left: 3%;
}

@media screen and (max-width: 768px) {
    .footer__center
    {
        padding-left: 0;
        width: 100%;
    }
}

.footer__right
{
    -webkit-box-ordinal-group: 3;
    -moz-box-ordinal-group: 3;
    -ms-flex-order: 3;
    -webkit-order: 3;
    order: 3;
    width: 22.5%;
    padding-left: 2em;
    padding-left: 3%;
}

@media screen and (max-width: 768px) {
    .footer__right
    {
        display: none;
    }
}

.footer .link
{
    color: #fff;
}

.box
{
    margin-bottom: 1em;
}

.box__body
{
    padding: 0 1em;
}

.box__island
{
    padding: 0 1em;
    background-color: #f9f9f9;

    padding: 1em;
    margin-top: .25em;

    border: rgba(0,0,0, .075) 1px solid;
}

.box__title
{
    font-size: 1.5em;
    font-weight: 400;
    margin: 0 0 .5em;
    padding: 0;
    color: #666;
}

.article
{
    font: 16px/1.5 'Noto Serif', Palatino Linotype, Palatino, serif;
    font-weight: 400;
}

.article__header
{
    padding: 0;
    margin: 0;
    color: #D24D33;
    font-size: 1.5em;
    font-weight: 400;
}

.article__header-link,
.article__header-link:link,
.article__header-link:hover,
.article__header-link:visited
{
    color: #D24D33;
}

.article__details
{
    font-size: .8em;
    color: #999;
}

.article__text code
{
    white-space: nowrap;
}

.article__text pre code
{
    white-space: pre;
}

.article__image
{
    display: block;
    border: rgba(0,0,0, 0.1) 1px solid;
    margin: 1em auto;
}

.comments
{
    margin-top: 2em;
}

.comments__header
{
    margin: 0;
    font-size: 1.5em;
}

.related
{
    margin-top: 2em;
}

.related__header
{
    margin: 0;
    font-size: 1.5em;
}

.related__text
{
}

.articles-list__item
{
    margin-bottom: 1em;
}

.articles-list__item p
{
    margin-top: 0.15em;
}

.recent-posts
{
    padding: 0;
}

.recent-posts__list
{
    list-style-type: none;
    margin: 0;
    padding: 0;
}

.recent-posts__item
{
    margin: 0;
    border-bottom: rgba(0,0,0, .075) 1px solid;
}

.recent-posts__date
{
    padding: 0.25em 0.25em 0.25em 1em;
    width: 6em;
    display: inline-block;
    text-align: right;
    border-right: rgba(0,0,0, .075) 1px solid;
    background-image: -webkit-linear-gradient(top, rgba(0,0,0, 0), rgba(255,255,255, 0.5));
}

.recent-posts__text-item
{
    padding: 0.25em 0.5em 0.25em 0.25em;
}

.sidebar
{
    -webkit-box-ordinal-group: 3;
    -moz-box-ordinal-group: 3;
    -ms-flex-order: 3;
    -webkit-order: 3;
    order: 3;
    width: 22.5%;
    padding-left: 2em;
    padding-left: 3%;
}

@media screen and (max-width: 768px) {
    .sidebar
    {
        -webkit-box-ordinal-group: 1;
        -moz-box-ordinal-group: 1;
        -ms-flex-order: 1;
        -webkit-order: 1;
        order: 1;
        width: 100%;
        margin-bottom: 1em;
        font-size: 1.25em;
        text-align: center;
        display: block;
    }

    .sidebar .menu-vert__title
    {
        display: none;
    }
    .sidebar .menu-vert__item
    {
        display: inline-block;
    }
    .sidebar .menu-vert__item:after
    {
        content: '';
        display: inline-block;
        width: 10px;
        height: 10px;
        background: rgba(0,0,0, 0.2);
        border-radius: 50%;
        margin: 0 1em;
    }

    .sidebar .menu-vert__item:last-child::after
    {
        display: none;
    }
}

.sidebar:before {
  content: '';
  width: 16em;
  display: block;
  height: 0;
}

.github
{
    margin-top: 1em;
}

@media screen and (max-width: 768px) {
    .github
    {
        display: none;
    }
}

.github_not-ready
{
    display: none;
}

.github__body {
    font-size: .8em;
    margin: 0;
    padding: 0;
    list-style: none;
}

.github__repo
{
    margin: 0;
    padding: 0;
    list-style: none;
}

.prompting
{
    -webkit-box-ordinal-group: 1;
    -moz-box-ordinal-group: 1;
    -ms-flex-order: 1;
    -webkit-order: 1;
    order: 1;
    width: 5.5%;
    display: block;
    padding-right: 2em;
    padding-right: 3%;
    text-align: right;
}

@media screen and (max-width: 768px) {
    .prompting
    {
        -webkit-box-ordinal-group: 3;
        -moz-box-ordinal-group: 3;
        -ms-flex-order: 3;
        -webkit-order: 3;
        order: 3;
        width: 100%;
        text-align: center;
        display: block;
    }
}

.prompting:before
{
    content: '';
    width: 4em;
    display: block;
    height: 0;
}

.candies_i
{
    display: inline-block;
    border-radius: 50%;
    margin-right: 4px;
}

